<h3>Default inputs</h3>
<div class="codePreview">
    <div class="g-form-element">
        <input type="text" placeholder="Formelement" />
    </div>

    <div class="g-form-element">
        <select>
            <option>Choose</option>
        </select>
    </div>
</div>

<h3>Time & dates</h3>
<div class="codePreview">

    <div class="g-form-element">
        <input type="time" placeholder="Formelement" />
    </div>

    <div class="g-form-element">
        <input type="date" placeholder="Formelement" />
    </div>

    <div class="g-form-element">
        <input type="month" placeholder="Formelement" />
    </div>

    <div class="g-form-element">
        <input type="datetime-local" placeholder="Formelement" />
    </div>

    <div class="g-form-element">
        <input type="week" placeholder="Formelement" />
    </div>

</div>





<div class="g-form-element">
    <textarea placeholder="Formelement"></textarea>
</div>

<div class="g-form-element">
    <input type="text" list="datalist" placeholder="Choose a car" />
    <datalist id="datalist">
        <option value="Mercedes">A German car</option>
        <option value="Volvo">A Swedish car</option>
    </datalist>
</div>

<div class="g-form-element g-form-element--file" data-label="Upload file">
    <input type="file" placeholder="Formelement" />
</div>



<div class="g-form-element">
    <input type="color" placeholder="Formelement" />
</div>





<button class="g-button">Submit</button>

<div class="g-form-element">
    <label>Formlabel</label>
    <input type="text" placeholder="Formelement" />
</div>