<label>
    <span class="g-switch g-switch--default">
        <input type="checkbox">
        <span class="g-switch__toggle"></span>
        <span class="g-switch__content">
            <span>Yes</span>
            <span>No</span>
        </span>
    </span>
</label>

<label>
    <span class="g-switch g-switch--success">
        <input type="checkbox" checked>
        <span class="g-switch__toggle"></span>
        <span class="g-switch__content">
            <span>On</span>
            <span>Off</span>
        </span>
    </span>
</label>

<label>
    <span class="g-switch">
        <input type="checkbox" checked>
        <span class="g-switch__toggle"></span>
        <span class="g-switch__content">
            <span><i class="uil uil-sun"></i></span>
            <span><i class="uil uil-moon"></i></span>
        </span>
    </span>
</label>

<label>
    <span class="g-switch g-switch--error">
        <input type="checkbox" checked>
        <span class="g-switch__toggle"></span>
        <span class="g-switch__content">
            <span><i class="uil uil-sun"></i></span>
            <span><i class="uil uil-moon"></i></span>
        </span>
    </span>
</label>